unsigned long SRAM_ADDRESS = 0x20000000;                       //   sourceAddress = ((sourceAddress | 0x20000000) & 0x2FFFFFFF);
unsigned long address = 0;

void Set_DMA_Increment_EBI(){

    DMACONSET = 0x00008000; // enable the DMA controller
    DCH0CON = 0x3; // channel off, priority 3, no chaining
    DCH0ECON = 0; // no start or stop IRQs, no pattern match        // DMA Channel 0_DMA0_VECTOR 134  PAGE 122 - 123 DATA SHEET PMP VECTOR
    DCH0ECONbits.CHSIRQ = 128;  //IRQ FOR PMP             Transfer Event (DCHxECON<15:8> bits (CHSIRQ<7:0>)) = PMP Write    //128 OFF128<17:1> IFS4<0> IEC4<0> IPC32<4:2> IPC32<1:0>
    DCH0ECONbits.SIRQEN = 1;
    DCH0ECON = 0x8010;
    //IEC0CLR=0x00010000; // disable DMA channel 0 interrupts
    IFS0CLR = 0x00010000; // clear existing DMA channel 0 interrupt flag
    CHPRI1_bit = 1;          // Channel Priority bits
    CHPRI0_bit = 1;          // 11 = Channel has priority 3 (highest)

    address = 0xA0000000 & 0x1FFFFFFF;  // Virtual to Physical Address Buffer For Picture Address 48.3.1 Virtual to Physical Address Calculation (and Vice-Versa) Page 14 Of Section 48 Memory Organization

    DCH0SSIZ = 2;           // Channel Source Size Bytes

    DCH0SSA  = address;     //: Transfer Source Physical Address  this works

    DCH0DSA  = SRAM_ADDRESS & 0x2FFFFFFF;   //:DESTINATION Address THIS WORKS TO INCREMENT EBI    //   destAddress = ((destAddress | 0x20000000) & 0x2FFFFFFF);
    //DCH0DSA =  address;

    DCH0DSIZ = 2;           //:Channel Destination Size Bytes Register

    DCH0CSIZ = 2;           //: DMA Channel 1 Cell Size Register  Channel Bytes Transferred Per Event

    DCH0CONSET = 0x80;      // Turn channel on

    CHAEN_bit = 1;       //: Channel Automatic Enable bit  Channel is continuously enabled

    IEC4SET = 0 | 0x40;    //Enable DMA channel interrupts

}


void DMA_EBI_Start_Increment(unsigned long x){
    x = x<<1;
    SRAM_ADDRESS = 0x20000000 | x;  //THIS WORKS TO INCREMENT EBI WHEN >> DCH0DSA  = SRAM_ADDRESS;
    DCH0DSA  = SRAM_ADDRESS;        //DESTINATION Address THIS WORKS TO INCREMENT EBI
    DCH0ECONBITS.CFORCE = 1;        //SET=0x00000080; // set CFORCE to 1
}

unsigned int Part_Capture_Start_Column = 80;
unsigned int Part_Capture_End_Column = 720;
unsigned int Part_Capture_Start_Row = 151;
unsigned int Part_Capture_End_Row = 333;  //331 is end row
unsigned int Small_Part_Capture_Start_Column = 575;

unsigned int dummybuffer = 0;

void Write_Screen_From_SRAM(unsigned int Start_Column, unsigned int End_Column, unsigned int Start_Row, unsigned int End_Row){

    unsigned long j=0;
    TFT_CS = 0;
    SRAM_CS = 0;

    Write_Command_SSD1963(0x2A);           //: Set Start Column               D/C = 0;
    Write_Data_SSD1963(Start_column>>8);   //: Start Column Number High Byte  D/C = 1;
    Write_Data_SSD1963(Start_column);      //: Start Column Number Low Byte   D/C = 1;
    Write_Data_SSD1963(End_Column>>8);     //: End Column Number High Byte    D/C = 1;
    Write_Data_SSD1963(End_Column);        //: End Column Number Low Byte     D/C = 1;

    Write_Command_SSD1963(0x2B);           //: SET ROW ADDRESS                D/C = 0;
    Write_Data_SSD1963(Start_Row>>8);      //: Start Row Number High Byte     D/C = 1;
    Write_Data_SSD1963(Start_Row);         //: Start Row Number Low Byte      D/C = 1;
    Write_Data_SSD1963(End_Row>>8);        //: End Row Number High Byte       D/C = 1;
    Write_Data_SSD1963(End_Row);           //: End Row Number Low Byte        D/C = 1;

    Write_Command_SSD1963(0x2C);   // Write Memory Start, 0x2C
    Write_Data_SSD1963(0x2C);   // Write Memory Start, 0x2C  9.30 Write Memory Continue    Command 0x3C

    PMCONbits.PTWREN = 0;   //WRITE STROBE ENABLE

    for (j=1; j<384001; j++){
      PMDOUT = dummybuffer;
      LATC3_Bit = 0;    //Write Bit Off
      LATC3_Bit = 1;    //Write Bit On
      DMA_EBI_Start_Increment(j);
      while(PMMODE & 0x8000); //Busy bit
     }

    TFT_CS = 1;
    DMA_EBI_Start_Increment(0);
    PMCONbits.PTWREN = 1;   //WRITE STROBE ENABLE
    SRAM_CS = 1;
}

unsigned int SRAM_Buffer2[27000];// absolute 0xA0000272;

void Small_Write_Screen(unsigned int Start_Column, unsigned int End_Column, unsigned int Start_Row, unsigned int End_Row){

    unsigned long j=0;
    TFT_CS = 0;

    Write_Command_SSD1963(0x2A);           //: Set Start Column               D/C = 0;
    Write_Data_SSD1963(Start_column>>8);   //: Start Column Number High Byte  D/C = 1;
    Write_Data_SSD1963(Start_column);      //: Start Column Number Low Byte   D/C = 1;
    Write_Data_SSD1963(End_Column>>8);     //: End Column Number High Byte    D/C = 1;
    Write_Data_SSD1963(End_Column);        //: End Column Number Low Byte     D/C = 1;

    Write_Command_SSD1963(0x2B);           //: SET ROW ADDRESS                D/C = 0;
    Write_Data_SSD1963(Start_Row>>8);      //: Start Row Number High Byte     D/C = 1;
    Write_Data_SSD1963(Start_Row);         //: Start Row Number Low Byte      D/C = 1;
    Write_Data_SSD1963(End_Row>>8);        //: End Row Number High Byte       D/C = 1;
    Write_Data_SSD1963(End_Row);           //: End Row Number Low Byte        D/C = 1;

    Write_Command_SSD1963(0x2C);           // Write Memory Start, 0x2C
    Write_Data_SSD1963(0x2C);              // Write Memory Start, 0x2C  9.30 Write Memory Continue    Command 0x3C

    for (j=2; j<26390; j++){               //  for (j=2; j<29300; j++){    26390 ???
      PMDOUT = SRAM_BUFFER2[j];
      while(PMMODE & 0x8000); //Busy bit
     }
    TFT_CS = 1;
}


void Small_Part_Screen_Capture(unsigned int Start_Column, unsigned int End_Column, unsigned int Start_Row, unsigned int End_Row){

    unsigned int j = 0;
    TFT_CS = 0;
    SRAM_CS = 1;

    Write_Command_SSD1963(0x2A);           //: Set Start Column               D/C = 0;
    Write_Data_SSD1963(Start_column>>8);   //: Start Column Number High Byte  D/C = 1;
    Write_Data_SSD1963(Start_column);      //: Start Column Number Low Byte   D/C = 1;
    Write_Data_SSD1963(End_Column>>8);     //: End Column Number High Byte    D/C = 1;
    Write_Data_SSD1963(End_Column);        //: End Column Number Low Byte     D/C = 1;

    Write_Command_SSD1963(0x2B);           //: SET ROW ADDRESS                D/C = 0;
    Write_Data_SSD1963(Start_Row>>8);      //: Start Row Number High Byte     D/C = 1;
    Write_Data_SSD1963(Start_Row);         //: Start Row Number Low Byte      D/C = 1;
    Write_Data_SSD1963(End_Row>>8);        //: End Row Number High Byte       D/C = 1;
    Write_Data_SSD1963(End_Row);           //: End Row Number Low Byte        D/C = 1;

    Write_Command_SSD1963(0x2E);           //: Read Memory Start
    PMCONbits.PTWREN = 0;   //WRITE STROBE ENABLE
    PMMODEbits.WAITM = 3;
    LATC3_bit = 1;

    for(j=0; j<26396; j++){        //for(j=0; j<29006; j++){
      SRAM_BUFFER2[j] = PMRDIN; //writing to a buffer  pmp read
      while(PMMODE & 0x8000); //Busy bit
     }
      TFT_CS = 1;
      PMCONbits.PTWREN = 1;   //WRITE STROBE ENABLE
      PMMODEbits.WAITM = 1;
}



